CREATE TABLE `asns` ( `id` int NOT NULL, `asn` varchar(256) NOT NULL, `description` varchar(1024) NOT NULL, `program_id` int NOT NULL, `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, `stat` varchar(50) DEFAULT NULL, `processed` varchar(50) DEFAULT NULL, `notes` varchar(255) DEFAULT NULL, UNIQUE KEY `asn` (`asn`,`program_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci